import React from 'react';
import Styled from 'styled-components';
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import Fade from 'react-reveal/Fade';


function About() {
  return (
    <div id='About'>
         <Wrap>
            <Fade top>
                <H1>
                <h1>Skills</h1>
                </H1>
                <Other>

            <Web>
                <h2>Web Development</h2>

                <span>HTML5, CSS3, JavaScript, React, Material-ui, Styled-components</span>

            </Web>
            <Programming>
                <h2>Languages</h2>

                <span>Python, C, Java, JavaScript</span>

            </Programming>
            <Database>
                <h2>Database</h2>

                <span>Firebase, SQL</span>

            </Database>
            </Other>
            <P>
            <p>Strong understanding of Data Structures and Algorithms, Object-Oriented Programming, Version-Control such as git/gitHub</p>
            </P>
            </Fade>
         </Wrap>
    </div>
  )
}

export default About

const Wrap = Styled.div`
display: flex;
flex-direction: column;
align-items: ;
width: 100vw;
color: black;
height: 100vh;
background-size: cover;
background-position: center;
background-repeat: no-repeat;
background: rgb(255,196,12);
overflow-x: hidden;
@media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
}
`
const Web = Styled.div`
display:flex;
flex-direction: column;
flex-wrap: nowrap;

padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
    margin: 0px;
}
`
const Database = Styled.div`
display:flex;
flex-direction: column;

flex-wrap: nowrap;

padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
    margin: 0px;
}
`
const Programming = Styled.div`
display:flex;
flex-direction: column;
flex-wrap: nowrap;

padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
    margin: 0px;
}
`
const H1 = Styled.div`
text-align: center;
`
const Other = Styled.div`
padding-top: 5vh;
`
const ItemText = Styled.div`
padding-top: 10vh;
`
const P = Styled.div`
font-size: 10px;
`
