import React from 'react';
import Styled from 'styled-components';
import Fade from 'react-reveal/Fade';
import SkillBox from './SkillBox';
import {BrowserRouter as Router} from 'react-router-dom';
import {HashLink as Link} from 'react-router-hash-link'

function About() {
  return (
    <div id='About'>
         <Wrap>
            <Fade top>
                <H1>
                <h1>Skills</h1>
                </H1>
                <Container>
                    <SkillBox 
                    img="/html.png"
                    name="HTML"/>
                    <SkillBox 
                    img="/css.png"
                    name="CSS"/>
                    <SkillBox 
                    img="/js.png"
                    name="JavaSript"/>
                    <SkillBox 
                    img="/react.png"
                    name="React"/>
                    <SkillBox 
                    img="/python.png"
                    name="Python"/>
                    <SkillBox 
                    img="/c.png"
                    name="C"/>
                    <SkillBox 
                    img="/sql.png"
                    name="SQL"/>
                    <SkillBox 
                    img="/firebase.png"
                    name="Firebase"/>
                    <SkillBox 
                    img="/mui.png"
                    name="Material-UI"/>
                    <SkillBox 
                    img="/ds.png"
                    name="Data Structures"/>
                    <SkillBox 
                    img="/algo.jpeg"
                    name="Algorithms"/>
                </Container>
                <Link to="#Projects">
                <DownArrow src = '/down-arrow.svg' />
                </Link>
            </Fade>

         </Wrap>
    </div>
  )
}

export default About

const Wrap = Styled.div`
display: flex;
flex-direction: column;
align-items: center;
width: 100vw;
color: black;
height: 100vh;
background-size: cover;
background-position: center;
background-repeat: no-repeat;
background: rgb(255,196,12);
overflow-x: hidden;
@media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
}
`


const H1 = Styled.div`
text-align: center;
`
const Container = Styled.div`
padding: 5vh;
margin: 5vh;
display: flex;
flex-direction: row;
justify-content: space-around;
flex-flow: wrap;
`


const DownArrow = Styled.img`
height: 40px;
overflow-x: hidden;
animation: animateDown infinite 1.5s;
padding-bottom: 20px;

`
