import React from 'react';
import Styled from 'styled-components';
import Fade from 'react-reveal/Fade';
import SkillBox from './SkillBox'


function About() {
  return (
    <div id='About'>
         <Wrap>
            <Fade top>
                <H1>
                <h1>Skills</h1>
                </H1>
                <Container>
                    <SkillBox 
                    img="/html.png"
                    name="HTML"/>
                    <SkillBox 
                    img="/css.png"
                    name="CSS"/>
                    <SkillBox 
                    img="/js.png"
                    name="JavaSript"/>
                    <SkillBox 
                    img="/react.png"
                    name="React"/>
                    <SkillBox 
                    img="/python.png"
                    name="Python"/>
                    <SkillBox 
                    img="/c.png"
                    name="C"/>

                </Container>
            </Fade>
         </Wrap>
    </div>
  )
}

export default About

const Wrap = Styled.div`
display: flex;
flex-direction: column;
align-items: ;
width: 100vw;
color: black;
height: 100vh;
background-size: cover;
background-position: center;
background-repeat: no-repeat;
background: rgb(255,196,12);
overflow-x: hidden;
@media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
}
`
const Web = Styled.div`
display:flex;
flex-direction: column;
flex-wrap: nowrap;

padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
    margin: 0px;
}
`
const Database = Styled.div`
display:flex;
flex-direction: column;

flex-wrap: nowrap;

padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
    margin: 0px;
}
`
const Programming = Styled.div`
display:flex;
flex-direction: column;
flex-wrap: nowrap;

padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
    margin: 0px;
}
`
const H1 = Styled.div`
text-align: center;
`
const Container = Styled.div`
padding-top: 5vh;
display: flex;
flex-direction: row;
justify-content: space-around;
flex-flow: wrap;
`
const ItemText = Styled.div`
padding-top: 10vh;
`
