import React from 'react';
import Styled from 'styled-components';
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import Fade from 'react-reveal/Fade';


function About() {
  return (
    <div id='About'>
         <Wrap>
            <Fade top>
            <Web>
                <h2>Web Development</h2>
                <ul>
                    <li>HTML5</li>
                    <li>CSS3</li>
                    <li>JavaScript</li>
                    <li>React</li>
                    <li>Material-ui</li>
                    <li>Styled-components</li>
                </ul>
            </Web>
            <Programming>
                <h2>Languages</h2>
                <ul>
                    <li>Python</li>
                    <li>C</li>
                    <li>Java</li>
                    <li>JavaScript</li>
                </ul>
            </Programming>
            <Database>
                <h2>Database</h2>
                <ul>
                    <li>Firebase</li>
                    <li>SQL</li>
                </ul>
            </Database>
            </Fade>
         </Wrap>
    </div>
  )
}

export default About

const Wrap = Styled.div`
display: flex;
flex-direction: row;
align-items: center;
justify-content: center;
width: 100vw;
color: black;
height: 100vh;
background-size: cover;
background-position: center;
background-repeat: no-repeat;
background-image: url('/background.jpg');
overflow-x: hidden;
@media (max-width: 768px) {
    flex-direction: column;
}
`
const Web = Styled.div`
display:flex;
flex-direction: column;
align-items: center;
flex-wrap: nowrap;
border: solid 2px rgb(255,196,12);
padding: 2vh;
margin: 0.5vh;
@media (max-width: 768px) {
    padding: 0vh;
}
`
const Database = Styled.div`
display:flex;
flex-direction: column;
align-items: center;
flex-wrap: nowrap;
border: solid 2px rgb(255,196,12);
padding: 2vh;
margin: 0.5vh;
`
const Programming = Styled.div`
display:flex;
flex-direction: column;
align-items: center;
flex-wrap: nowrap;
border: solid 2px rgb(255,196,12);
padding: 2vh;
margin: 0.5vh;
`
const ItemText = Styled.div`
padding-top: 10vh;
`
